import { useEffect, useState } from "react";
import axios from "axios";

const History = () => {
  const [history, setHistory] = useState<any[]>([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const res = await axios.get("http://localhost:8002/my-bookings", {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
    });

    // Tambahkan field dummy "paid": false (nanti akan di-replace dgn status)
    const bookings = res.data.map((b: any) => ({
      ...b,
      amount: b.quantity * 100000,
      paid: false,
    }));

    // Cek status pembayaran per booking
    for (let booking of bookings) {
      try {
        const res = await axios.get(`http://localhost:8003/status/${booking.id}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        booking.paid = res.data.status === "PAID";
      } catch {
        booking.paid = false;
      }
    }

    setHistory(bookings);
  };

  const handlePay = async (bookingId: number, amount: number) => {
    try {
      await axios.post(
        "http://localhost:8003/pay",
        { booking_id: bookingId, amount },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      alert("Pembayaran berhasil!");
      fetchData(); // Refresh status
    } catch {
      alert("Gagal membayar");
    }
  };

  return (
    <div style={{ padding: 40 }}>
      <h2>Riwayat Pemesanan</h2>
      {history.map((b) => (
        <div key={b.id} style={{ border: "1px solid #ccc", padding: 10, marginBottom: 10 }}>
          <div><strong>Konser:</strong> {b.concert}</div>
          <div><strong>Jumlah:</strong> {b.quantity}</div>
          <div><strong>Total:</strong> Rp {b.amount.toLocaleString()}</div>
          <div><strong>Status:</strong> {b.paid ? "Sudah Dibayar" : "Belum Dibayar"}</div>
          {!b.paid && (
            <button onClick={() => handlePay(b.id, b.amount)}>Bayar Sekarang</button>
          )}
        </div>
      ))}
    </div>
  );
};

export default History;
