import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { logout } from "../store/userSlice";
import Swal from 'sweetalert2';

interface Concert {
  id: number;
  name: string;
  location: string;
  date: string;
  available_tickets: number;
}

const Dashboard = () => {
  const [concerts, setConcerts] = useState<Concert[]>([]);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const handleLogout = () => {
    Swal.fire({
        title: "Yakin ingin logout?",
        icon: "warning",
        showCancelButton: true,
        confirmButtonText: "Ya, logout",
    }).then((result) => {
        if (result.isConfirmed) {
        dispatch(logout());
        navigate("/");
        }
    });
  };

  useEffect(() => {
    axios
      .get("http://localhost:8002/concerts")
      .then((res) => setConcerts(res.data))
      .catch((err) => console.error("Error fetching concerts:", err));
  }, []);

  return (
    <div style={{ padding: 40 }}>
      <div style={{ marginBottom: 20 }}>
        <button onClick={handleLogout}>Logout</button>
      </div>

      <h2>List Konser</h2>
      {concerts.length === 0 && <p>Loading konser...</p>}

      {concerts.map((c) => (
        <div key={c.id} style={{ marginBottom: 20, borderBottom: "1px solid #ccc", paddingBottom: 10 }}>
          <strong>{c.name}</strong> - {new Date(c.date).toLocaleString()} <br />
          Lokasi: {c.location} <br />
          Tiket tersedia: {c.available_tickets} <br />
          <button onClick={() => navigate(`/book/${c.id}`)}>Pesan Tiket</button>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
